const LOAD = 'spots/LOAD'
const CREATE = 'spots/CREATE'
// const DELETE = 'spots/DELETE'

const load = spots => ({
    type: LOAD,
    spots
})

const create = spot => ({
    type: CREATE,
    spot
})

// const getOne = id => ({

// })

// const remove = spotId => ({
//     type: DELETE,
//     spotId
// })

export const getSpots = () => async dispatch =>{
    const response = await fetch('/api/spots');

    if (response.ok){
        const spots = await response.json();
        dispatch(load(spots))
        return spots
    }
}

export const postSpot = (payload) => async dispatch => {
    const response = await fetch('/api/spots', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify(payload)
    });
    if(response.ok){
        const spot = await response.json();
        dispatch(create(spot))
        return spot
    }
}

export const putSpot = (payload) => async dispatch => {
    const response = await fetch(`/api/spots/${payload.id}`,{
        method: 'PUT',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify(payload)
    });

    if(response.ok) {
        const spot = await response.json();
        dispatch(create(spot))
        return spot
    }
}

const intialState = {spots:{}}

const spotReducer = (state = intialState, action) => {
    switch (action.type) {
        case LOAD:

            return null
        case CREATE:
            return {
                ...state, spots: [...state.spots, action.spot]
            }
            default:
                return state
    }
}

export default spotReducer
