import { useEffect, useState } from "react"
import { useSelector } from "react-redux"

function searchSpots(){}

function SearchBar (){
    const [searchQuery, setSearchQuery] = useState('')
    const [searchResults, setSearchResults] = useState([])
    const allSpots = Object.values(useSelector(state => state.spots.allSpots))

    const handleInput= (e)=>{
        const value = e.target.value
        setSearchQuery(value)
        let results = []
        allSpots.forEach((spot)=>{
            if(spot.city.toLowerCase().includes(value.toLowerCase())|| spot.state.toLowerCase().includes(value.toLowerCase()) || spot.country.toLowerCase().includes(value.toLowerCase())){
                results.push(spot)
            }
        })
        setSearchResults([results])
    }

    console.log(searchResults)

    return(
        <div className="search-container">
            <input type="search" onChange={handleInput} value={searchQuery}></input>
            <div>

            </div>
        </div>
    )
}

export default SearchBar
