            <div className='review-content'>
                <p className='review line-clamp-3'>{review.review}</p>
            </div>
        </div>
        <button onclick={document.querySelector('p').classList.remove('line-clamp-3')}>Read more...</button>

        <button onclick="document.querySelector('p').classList.add('line-clamp-3')">Read less...</button>

        {truncate()}
</div>
    )
    else return null
}

export default ReviewCard
